// Log candidate validation results
const candidateData = $('Prepare Candidate Data').first().json;
const validationResults = $input.first().json;

const logEntry = {
  timestamp: new Date().toISOString(),
  candidateName: candidateData.candidateName,
  candidateEmail: candidateData.candidateEmail,
  positionApplied: candidateData.positionApplied,
  overallStatus: validationResults.overallStatus,
  validationScore: validationResults.validationScore,
  trustScore: validationResults.trustScore,
  cvAuthenticityScore: validationResults.cvAuthenticityScore,
  signalValidationScore: validationResults.signalValidationScore,
  recommendations: validationResults.recommendations,
  redFlags: validationResults.redFlags || [],
  strengths: validationResults.strengths || [],
  emailSent: true
};

console.log('Candidate Validation Complete:', JSON.stringify(logEntry, null, 2));

return [{ json: logEntry }];
